    public static  void  minPair(List<Integer> numbers){
         //1. Sort
        Collections.sort(numbers);

        //Group by diff
        Map<Integer, List<Integer[]>> map =  new HashMap<>();
        for(int i = 0; i < numbers.size() -1; i++){
            int diff = numbers.get(i) -  numbers.get(i+1);
            int absDiff = Math.abs(diff);
            if(!map.containsKey(absDiff)){
                map.put(absDiff, new ArrayList<Integer[]>());
            }
            map.get(absDiff).add(new Integer[]{numbers.get(i), numbers.get(i+1)});
        }
        int min = Integer.MAX_VALUE;
        List<Integer[]>ans = new ArrayList<>();
        for(Integer diff :  map.keySet()){
            if(diff <  min){
                ans = map.get(diff);
                min = diff;
            }
        }
        for(Integer[] pair : ans){
            System.out.println(pair[0] + " " + pair[1]);
        }

    }
